import React, {
  useEffect,
  useState,
  useImperativeHandle,
  forwardRef,
} from "react";
import { useNavigate } from "react-router-dom";
import {
  fetchDebtsList,
  updateDebt,
  deleteDebt,
} from "../utils/debtsService";
import "../styles/TablePage.css";

const TablePage = forwardRef(function TablePage({ setEditPerson }, ref) {
  const [data, setData] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [usingCache, setUsingCache] = useState(false);
  const navigate = useNavigate();

  // üîÑ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
  const fetchData = async () => {
    setIsLoading(true);
    try {
      const result = await fetchDebtsList();
      setData(result.data);
      setUsingCache(result.source === "cache");
    } catch (err) {
      console.error("Error fetching data:", err);
      setUsingCache(false);
    } finally {
      setIsLoading(false);
    }
  };

  // üëá ÿßŸÑÿ≥ŸÖÿßÿ≠ ŸÑŸÄ Home ÿ®ÿßÿ≥ÿ™ÿØÿπÿßÿ° ÿßŸÑÿ™ÿ≠ÿØŸäÿ´
  useImperativeHandle(ref, () => ({
    refreshTable: fetchData,
  }));

  useEffect(() => {
    fetchData();
  }, []);

  // ‚úèÔ∏è ÿ™ÿπÿØŸäŸÑ
  const handleEdit = (person) => {
    setEditPerson(person);
    navigate("/home");
    setTimeout(() => {
      window.scrollTo({ top: 0, behavior: "smooth" });
    }, 200);
  };

  // üí∞ ÿ™ÿ≥ÿØŸäÿØ
  const handleMarkPaid = async (person) => {
    try {
      const updatedPerson = { ...person, paid: true };
      setData((prev) =>
        prev.map((entry) =>
          entry._id === person._id ? { ...entry, paid: true } : entry
        )
      );
      const result = await updateDebt(person._id, updatedPerson);
      if (result.queued) {
        alert("ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿßŸÑÿ™ÿπÿØŸäŸÑ ŸÖÿ≠ŸÑŸäŸãÿßÿå Ÿàÿ≥Ÿäÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑŸá ÿπŸÜÿØ ÿ™ŸàŸÅÿ± ÿßŸÑÿ•ŸÜÿ™ÿ±ŸÜÿ™.");
      }
      await fetchData();
      if (!result.queued) {
        const message = `ŸÖÿ±ÿ≠ÿ®Ÿãÿß ${person.name}ÿå ÿ™ŸÖ ÿ™ÿ≥ÿØŸäÿØ ŸÅÿßÿ™Ÿàÿ±ÿ™ŸÉ ÿ®ŸÜÿ¨ÿßÿ≠ \nÿ¥ŸÉÿ±Ÿãÿß ŸÑÿ™ÿ≥ÿØŸäÿØŸÉ `;
        const url = `https://wa.me/${person.phone}?text=${encodeURIComponent(
          message
        )}`;
        window.open(url, "_blank");
      }
    } catch (err) {
      console.error("Error marking as paid:", err);
    }
  };

  // ‚Ü©Ô∏è ÿ•ÿ±ÿ¨ÿßÿπ ŸÑÿ∫Ÿäÿ± ŸÖÿØŸÅŸàÿπ
  const handleMarkUnpaid = async (person) => {
    try {
      setData((prev) =>
        prev.map((entry) =>
          entry._id === person._id ? { ...entry, paid: false } : entry
        )
      );
      const updatedPerson = { ...person, paid: false };
      const result = await updateDebt(person._id, updatedPerson);
      if (result.queued) {
        alert("ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿßŸÑÿ™ÿπÿØŸäŸÑ ŸÖÿ≠ŸÑŸäŸãÿßÿå Ÿàÿ≥Ÿäÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑŸá ÿπŸÜÿØ ÿ™ŸàŸÅÿ± ÿßŸÑÿ•ŸÜÿ™ÿ±ŸÜÿ™.");
      } else {
        alert(`‚Ü©Ô∏è ${person.name} ÿπÿßÿØ ŸÑŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ∫Ÿäÿ± ÿØÿßŸÅÿπŸäŸÜ.`);
      }
      await fetchData();
    } catch (err) {
      console.error("Error marking as unpaid:", err);
    }
  };

  // üí¨ ÿ™ÿ∞ŸÉŸäÿ±
  const handleSendReminder = (person) => {
    const message = `ŸÖÿ±ÿ≠ÿ®Ÿãÿß ${person.name} ÿå ŸÜÿ∞ŸÉŸëÿ±ŸÉ ÿ£ŸÜ ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÖÿ≥ÿ™ÿ≠ŸÇ (${person.amount}$) ŸÑŸÖ Ÿäÿ™ŸÖ ÿØŸÅÿπŸá ÿ®ÿπÿØ. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑÿ™ÿ¨ÿØŸäÿØ ŸÅŸä ÿ£ŸÇÿ±ÿ® ŸàŸÇÿ™ `;
    const url = `https://wa.me/${person.phone}?text=${encodeURIComponent(
      message
    )}`;
    window.open(url, "_blank");
  };

  // üóëÔ∏è ÿ≠ÿ∞ŸÅ
  const handleDelete = async (id) => {
    if (window.confirm("ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ÿ∞ŸÅ Ÿáÿ∞ÿß ÿßŸÑÿ≤ÿ®ŸàŸÜÿü")) {
      try {
        const result = await deleteDebt(id);
        if (result.queued) {
          alert("ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿßŸÑÿ≠ÿ∞ŸÅ ŸÖÿ≠ŸÑŸäŸãÿßÿå Ÿàÿ≥Ÿäÿ™ŸÖ ÿ™ŸÜŸÅŸäÿ∞Ÿá ÿπŸÜÿØ ÿ™ŸàŸÅÿ± ÿßŸÑÿ•ŸÜÿ™ÿ±ŸÜÿ™.");
        }
        await fetchData();
      } catch (err) {
        console.error("Error deleting data:", err);
      }
    }
  };

  const filteredData = data.filter(
    (item) =>
      item.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      item.phone?.includes(searchTerm) ||
      item.boxName?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="products-table-container">
      <h3>üìã ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ≤ÿ®ÿßÿ¶ŸÜ</h3>
      <input
        type="text"
        className="search-input responsive"
        placeholder="üîç ÿ®ÿßŸÑÿßÿ≥ŸÖ ÿ£Ÿà ÿßŸÑÿ±ŸÇŸÖ ÿ£Ÿà ÿßÿ≥ŸÖ ÿßŸÑÿπŸÑÿ®ÿ©..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {usingCache && (
        <p className="offline-indicator">
