import React, { useEffect, useState } from "react";
import "../styles/TablePage.css";
import {
  fetchDebtsList,
  updateDebt,
  deleteDebt,
} from "../utils/debtsService";
import whatsappApi from "../utils/whatsappApi";

function NonPayers() {
  const [data, setData] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [usingCache, setUsingCache] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [singleSendingId, setSingleSendingId] = useState(null);
  const [delaySeconds] = useState(60);

  useEffect(() => {
    fetchData();
  }, []);

  // üß© ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ÿ¥ÿÆÿßÿµ ÿ∫Ÿäÿ± ÿßŸÑŸÖÿØŸÅŸàÿπŸäŸÜ ŸÅŸÇÿ∑
  const fetchData = async () => {
    setIsLoading(true);
    try {
      const result = await fetchDebtsList();
      const unpaidUsers = result.data.filter((item) => !item.paid);
      setData(unpaidUsers);
      setUsingCache(result.source === "cache");
    } catch (err) {
      console.error("Error fetching data:", err);
      setUsingCache(false);
    } finally {
      setIsLoading(false);
    }
  };

  const normalizePhoneNumber = (phone) =>
    phone?.toString().replace(/[^0-9]/g, "") || "";

  const buildReminderPayload = (person) => {
    const amountDisplay =
      typeof person.amount === "number" ? `${person.amount}$` : person.amount || "";
    const message = `ŸÖÿ±ÿ≠ÿ®Ÿãÿß ${person.name}ÿå ŸÜÿ∞ŸÉŸëÿ±ŸÉ ÿ£ŸÜ ÿßŸÑŸÖÿ®ŸÑÿ∫ (${amountDisplay}) ŸÑŸÖ Ÿäÿ™ŸÖ ÿØŸÅÿπŸá ÿ®ÿπÿØ.`;
    return {
      phone: normalizePhoneNumber(person.phone),
      message,
    };
  };

  const sendViaWhatsappApi = async (payload) => {
    if (!payload.phone) throw new Error("ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ ÿ∫Ÿäÿ± ÿµÿßŸÑÿ≠");
    const response = await whatsappApi.post("/send_reminder", payload);
    return response?.data;
  };

  const openWaLink = (payload, closeAfterMs = 60000) => {
    if (!payload?.phone) return;
    const url = `https://web.whatsapp.com/send?phone=${payload.phone}&text=${encodeURIComponent(
      payload.message
    )}`;
    const newTab = window.open(url, "_blank");
    if (newTab && closeAfterMs > 0) {
      setTimeout(() => {
        try {
          newTab.close();
        } catch {
          /* ignore */
        }
      }, closeAfterMs);
    }
  };

  // üí¨ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ™ÿ∞ŸÉŸäÿ± Ÿàÿßÿ™ÿ≥ÿßÿ®
  const handleSendReminder = async (person) => {
    if (!person?.phone) {
      alert("ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±.");
      return;
    }
    const payload = buildReminderPayload(person);
    setSingleSendingId(person._id);
    try {
      await sendViaWhatsappApi(payload);
      alert(`‚úÖ ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ™ÿ∞ŸÉŸäÿ± ÿ•ŸÑŸâ ${person.name}`);
    } catch (err) {
      console.error("Error sending WhatsApp reminder:", err);
      alert("‚ö†Ô∏è ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿÆÿßÿØŸÖ ÿßŸÑÿ•ÿ±ÿ≥ÿßŸÑÿå ÿ≥Ÿäÿ™ŸÖ ŸÅÿ™ÿ≠ Ÿàÿßÿ™ÿ≥ÿßÿ® ŸäÿØŸàŸäŸãÿß.");
      openWaLink(payload, delaySeconds * 1000);
    } finally {
      setSingleSendingId(null);
    }
  };

  // üóëÔ∏è ÿ≠ÿ∞ŸÅ ÿ≤ÿ®ŸàŸÜ
  const handleDelete = async (id) => {
    if (window.confirm("ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ÿ∞ŸÅ Ÿáÿ∞ÿß ÿßŸÑÿ≤ÿ®ŸàŸÜÿü")) {
      try {
        setData((prev) => prev.filter((item) => item._id !== id));
        const result = await deleteDebt(id);
        if (result.queued) {
          alert("‚ö†Ô∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ•ŸÜÿ™ÿ±ŸÜÿ™. ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿßŸÑÿ≠ÿ∞ŸÅ ŸÖÿ≠ŸÑŸäŸãÿß Ÿàÿ≥ŸäŸèÿ±ÿ≥ŸÑ ÿ™ŸÑŸÇÿßÿ¶ŸäŸãÿß ŸÑÿßÿ≠ŸÇŸãÿß.");
        }
        await fetchData();
      } catch (err) {
        console.error("Error deleting data:", err);
      }
    }
  };

  // üí∞ ÿ™ÿ≥ÿØŸäÿØ ÿßŸÑŸÖÿ®ŸÑÿ∫
  const handleMarkPaid = async (person) => {
    try {
      const updatedPerson = { ...person, paid: true };
      setData((prev) => prev.filter((item) => item._id !== person._id));
      const result = await updateDebt(person._id, updatedPerson);
      if (result.queued) {
        alert("‚ö†Ô∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ•ŸÜÿ™ÿ±ŸÜÿ™. ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿßŸÑÿ™ÿπÿØŸäŸÑ ŸÖÿ≠ŸÑŸäŸãÿß Ÿàÿ≥ŸäŸèÿ±ÿ≥ŸÑ ÿ™ŸÑŸÇÿßÿ¶ŸäŸãÿß ŸÑÿßÿ≠ŸÇŸãÿß.");
      } else {
        alert(`‚úÖ ${person.name} ÿ™ŸÖ ÿ™ÿ≠ŸàŸäŸÑŸá ÿ•ŸÑŸâ ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿØÿßŸÅÿπŸäŸÜ`);
      }
      await fetchData();
    } catch (err) {
      console.error("Error marking as paid:", err);
    }
  };

  // üîé ŸÅŸÑÿ™ÿ±ÿ© ÿ≠ÿ≥ÿ® ÿßŸÑÿ®ÿ≠ÿ´
  const filteredData = data.filter(
    (item) =>
      item.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      item.phone?.includes(searchTerm) ||
      item.boxName?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="products-table-container">
      <h3>‚ùå ÿßŸÑÿ∫Ÿäÿ± ÿØÿßŸÅÿπŸäŸÜ</h3>

      <input
        type="text"
        className="search-input responsive"
        placeholder="üîç ÿßÿ®ÿ≠ÿ´ ÿ®ÿßŸÑÿßÿ≥ŸÖ ÿ£Ÿà ÿßŸÑÿ±ŸÇŸÖ ÿ£Ÿà ÿßŸÑÿπŸÑÿ®ÿ©..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

