import React, { useEffect, useMemo, useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Users.css";
import { addUser, loadUsers, removeUser } from "../utils/userStorage";

const initialForm = {
  fullName: "",
  username: "",
  phone: "",
  email: "",
  notes: "",
};

function Users() {
  const [users, setUsers] = useState([]);
  const [form, setForm] = useState(initialForm);
  const [searchTerm, setSearchTerm] = useState("");
  const [statusMessage, setStatusMessage] = useState("");

  useEffect(() => {
    setUsers(loadUsers());
  }, []);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const resetForm = () => setForm(initialForm);

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!form.fullName.trim() || !form.username.trim()) {
      setStatusMessage("ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑŸÉÿßŸÖŸÑ Ÿàÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ≠ŸÇŸàŸÑ ÿ•ŸÑÿ≤ÿßŸÖŸäÿ©.");
      return;
    }
    const payload = {
      ...form,
      username: form.username.trim().toLowerCase(),
    };
    const newUser = addUser(payload);
    setUsers((prev) => [...prev, newUser]);
    resetForm();
    setStatusMessage(`‚úÖ ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿµŸÅÿ≠ÿ© ÿÆÿßÿµÿ© ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ${newUser.fullName}`);
  };

  const handleDelete = (id) => {
    if (!window.confirm("ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ≠ÿ∞ŸÅ Ÿáÿ∞ÿß ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖÿü")) return;
    const next = removeUser(id);
    setUsers(next);
  };

  const filteredUsers = useMemo(() => {
    const term = searchTerm.trim().toLowerCase();
    if (!term) return users;
    return users.filter(
      (user) =>
        user.fullName?.toLowerCase().includes(term) ||
        user.username?.toLowerCase().includes(term) ||
        user.phone?.includes(term)
    );
  }, [users, searchTerm]);

  return (
    <div className="users-page">
      <section className="users-form-card">
        <h2>ÿ•ŸÜÿ¥ÿßÿ° ŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ¨ÿØŸäÿØ</h2>
        <p>ŸÉŸÑ ŸÖÿ≥ÿ™ÿÆÿØŸÖ Ÿäÿ≠ÿµŸÑ ÿπŸÑŸâ ÿµŸÅÿ≠ÿ© ÿÆÿßÿµÿ© ÿ™ÿ™ÿ∂ŸÖŸÜ ÿ®ŸäÿßŸÜÿßÿ™Ÿá ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ©.</p>

        <form className="users-form" onSubmit={handleSubmit}>
          <div className="form-grid">
            <label>
              ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑŸÉÿßŸÖŸÑ*
              <input
                type="text"
                name="fullName"
                value={form.fullName}
                onChange={handleChange}
                placeholder="ŸÖÿ´ÿßŸÑ: ŸÖÿ≠ŸÖÿØ ÿ£ÿ≠ŸÖÿØ"
                required
              />
            </label>
            <label>
              ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ*
              <input
                type="text"
                name="username"
                value={form.username}
                onChange={handleChange}
                placeholder="user-name"
                required
              />
            </label>
            <label>
              ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ
              <input
                type="tel"
                name="phone"
                value={form.phone}
                onChange={handleChange}
                placeholder="71XXXXXX"
              />
            </label>
            <label>
              ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä
              <input
                type="email"
                name="email"
                value={form.email}
                onChange={handleChange}
                placeholder="example@email.com"
              />
            </label>
          </div>

          <label>
            ŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ©
            <textarea
              name="notes"
              rows="3"
              value={form.notes}
              onChange={handleChange}
              placeholder="ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿ™ŸáŸÖŸÉ ÿπŸÜ Ÿáÿ∞ÿß ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ..."
            />
          </label>

          <button type="submit" className="btn-primary">
            ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿµŸÅÿ≠ÿ© ÿßŸÑÿÆÿßÿµÿ©
          </button>
        </form>

        {statusMessage && <p className="status-message">{statusMessage}</p>}
      </section>

      <section className="users-list-card">
        <div className="list-header">
          <h3>ÿ¨ŸÖŸäÿπ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ</h3>
          <input
            type="text"
            placeholder="üîç ÿßÿ®ÿ≠ÿ´ ÿ®ÿßŸÑÿßÿ≥ŸÖ ÿ£Ÿà ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ£Ÿà ÿßŸÑŸáÿßÿ™ŸÅ"
            value={searchTerm}
            onChange={(event) => setSearchTerm(event.target.value)}
          />
        </div>

        {filteredUsers.length === 0 ? (
          <p className="empty-state">ŸÑŸÖ Ÿäÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿ£Ÿä ŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ≠ÿ™Ÿâ ÿßŸÑÿ¢ŸÜ.</p>
        ) : (
          <div className="users-grid">
            {filteredUsers.map((user) => (
              <article key={user.id} className="user-card">
                <div>
                  <h4>{user.fullName}</h4>
                  <p className="username">@{user.username}</p>
                  {user.phone && <p>üìû {user.phone}</p>}
                  {user.email && <p>‚úâÔ∏è {user.email}</p>}
                </div>
                <div className="card-actions">
                  <Link to={`/users/${user.id}`} className="btn-secondary">
                    ÿπÿ±ÿ∂ ÿßŸÑÿµŸÅÿ≠ÿ©
                  </Link>
                  <button className="btn-danger" onClick={() => handleDelete(user.id)}>
                    ÿ≠ÿ∞ŸÅ
                  </button>
                </div>
              </article>
            ))}
          </div>
        )}
      </section>
    </div>
  );
}

export default Users;
