import React, { useEffect, useState } from "react";
import "../styles/TablePage.css";
import {
  fetchDebtsList,
  deleteDebt,
} from "../utils/debtsService";

function Payers() {
  const [data, setData] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [usingCache, setUsingCache] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  // üì¶ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ÿ¥ÿÆÿßÿµ ÿßŸÑÿ∞ŸäŸÜ ÿØŸÅÿπŸàÿß ŸÅŸÇÿ∑
  const fetchData = async () => {
    setIsLoading(true);
    try {
      const result = await fetchDebtsList();
      const paidUsers = result.data.filter((item) => item.paid);
      setData(paidUsers);
      setUsingCache(result.source === "cache");
    } catch (err) {
      console.error("Error fetching data:", err);
      setUsingCache(false);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // üí¨ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© Ÿàÿßÿ™ÿ≥ÿßÿ®
  const handleSendWhatsApp = (person) => {
    const message = `ŸÖÿ±ÿ≠ÿ®Ÿãÿß ${person.name} ÿå ÿ¥ŸÉÿ±Ÿãÿß ŸÑÿ™ÿ≥ÿØŸäÿØŸÉ ÿßŸÑŸÖÿ®ŸÑÿ∫ (${person.amount}$) ŸÑŸÑÿπŸÑÿ®ÿ© ${person.boxName} `;
    const url = `https://wa.me/${person.phone}?text=${encodeURIComponent(message)}`;
    window.open(url, "_blank");
  };

  // üóëÔ∏è ÿ≠ÿ∞ŸÅ ÿ≤ÿ®ŸàŸÜ
  const handleDelete = async (id) => {
    if (window.confirm("ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ÿ∞ŸÅ Ÿáÿ∞ÿß ÿßŸÑÿ≤ÿ®ŸàŸÜÿü")) {
      try {
        setData((prev) => prev.filter((item) => item._id !== id));
        const result = await deleteDebt(id);
        if (result.queued) {
          alert("‚ö†Ô∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ•ŸÜÿ™ÿ±ŸÜÿ™. ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿßŸÑÿ≠ÿ∞ŸÅ ŸÖÿ≠ŸÑŸäŸãÿß Ÿàÿ≥ŸäŸèÿ±ÿ≥ŸÑ ÿ™ŸÑŸÇÿßÿ¶ŸäŸãÿß ŸÑÿßÿ≠ŸÇŸãÿß.");
        }
        await fetchData();
      } catch (err) {
        console.error("Error deleting data:", err);
      }
    }
  };

  // üîé ŸÅŸÑÿ™ÿ±ÿ© ÿ≠ÿ≥ÿ® ÿßŸÑÿ®ÿ≠ÿ´
  const filteredData = data.filter(
    (item) =>
      item.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      item.phone?.includes(searchTerm) ||
      item.boxName?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="products-table-container">
      <h3>‚úÖ ÿßŸÑÿØÿßŸÅÿπŸàŸÜ</h3>

      <input
        type="text"
        className="search-input responsive"
        placeholder="üîç ÿßÿ®ÿ≠ÿ´ ÿ®ÿßŸÑÿßÿ≥ŸÖ ÿ£Ÿà ÿßŸÑÿ±ŸÇŸÖ ÿ£Ÿà ÿßŸÑÿπŸÑÿ®ÿ©..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {usingCache && (
        <p className="offline-indicator">
          ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿπÿ±Ÿàÿ∂ÿ© ŸÖŸÜ ÿßŸÑÿ∞ÿßŸÉÿ±ÿ© ÿßŸÑŸÖÿ§ŸÇÿ™ÿ© ÿ®ÿ≥ÿ®ÿ® ÿßŸÜŸÇÿ∑ÿßÿπ ÿßŸÑÿ•ŸÜÿ™ÿ±ŸÜÿ™.
        </p>
      )}

      {isLoading && (
        <div className="loading-indicator" role="status">
          <span className="spinner" aria-hidden="true" />
          <span>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...</span>
        </div>
      )}

      {!isLoading && filteredData.length === 0 ? (
        <p className="no-products">ŸÖÿß ŸÅŸä ÿ≠ÿØÿß ÿØÿßŸÅÿπ ÿ®ÿπÿØ üí∏</p>
      ) : (
        <table className="products-table">
          <tbody>
            {filteredData.map((person) => (
              <tr key={person._id} className="paid-row">
                <td><strong> ÿßŸÑÿßÿ≥ŸÖ:</strong> {person.name || "‚Äî"}</td>
                <td><strong> ÿßŸÑŸáÿßÿ™ŸÅ:</strong> {person.phone || "‚Äî"}</td>
                <td><strong> ÿßŸÑÿπŸÑÿ®ÿ©:</strong> {person.boxName || "‚Äî"}</td>
                <td><strong> ÿßŸÑŸÖÿ®ŸÑÿ∫:</strong> <span className="product-price">{person.amount ? `$${person.amount}` : "‚Äî"}</span></td>

                <td>
                  <span className="product-status available">‚úÖ ÿ™ŸÖ ÿßŸÑÿ™ÿ≥ÿØŸäÿØ</span>
                </td>
                <td>
                  <div className="table-actions">
                    <button className="view-btn" onClick={() => handleSendWhatsApp(person)}>
                      üí¨ WhatsApp
                    </button>
                    <button className="delete-btn" onClick={() => handleDelete(person._id)}>
                      üóëÔ∏è ÿ≠ÿ∞ŸÅ
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Payers;
